sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    try {
        var jsonString = current.u_string_full_utf8_1; 
        gs.info('JSON String: ' + jsonString); // Log the JSON string

        // Validate JSON string before parsing
        if (!jsonString) {
            gs.addErrorMessage('The JSON string is empty.');
            return;
        }

        var jsonObj;
        try {
            jsonObj = JSON.parse(jsonString);
        } catch (e) {
            gs.error('Error parsing JSON: ' + e.message);
            gs.addErrorMessage('Error parsing JSON: ' + e.message);
            return;
        }

        gs.info('Parsed JSON Object: ' + JSON.stringify(jsonObj)); // Log the parsed JSON object

        var prod_name = current.sys_id; // Get the sys_id of the current ERP product

        for (var i = 0; i < jsonObj.length; i++) {
            var Name = jsonObj[i]['Name'];
            var Type = jsonObj[i]['Type'];
            var Value = jsonObj[i]['Value']; // Store the value without assigning it to any field

            // Check if the characteristic already exists
            var gr = new GlideRecord('x_1166825_erp_assi_erp_product_characteristics');
            gr.addQuery('name', Name);
            gr.addQuery('type', Type);
            gr.query();

            var characterSysId;
            if (gr.next()) {
                characterSysId = gr.sys_id; // Get the existing sys_id
                gs.info('Existing characteristic found: ' + characterSysId);
            } else {
                // Insert new record into the product characteristics table
                gr.initialize();
                gr.setValue('name', Name); // Assuming 'name' is the field name
                gr.setValue('type', Type); // Assuming 'type' is the field name
                characterSysId = gr.insert();
                gs.info('New characteristic created: ' + characterSysId);
            }

            // Insert record into the relationship table
            var gar = new GlideRecord('x_1166825_erp_assi_m2m_x_1166825_er_x_1166825_er');
            gar.initialize();
            gar.setValue('x_1166825_erp_assi_erp_product', prod_name); // Set the reference field with the sys_id of the product
            gar.setValue('x_1166825_erp_assi_erp_product_character', characterSysId); // Set the reference field with the sys_id of the characteristic
            gar.setValue('product_characteristic_value', Value); // Set the value
            gar.insert();
        }
    } catch (e) {
        gs.error('Error: ' + e.message);
        gs.addErrorMessage('Error: ' + e.message);
    }

})(current, previous);
