sys_transform_script.script:
(function runTransformScript(source, map, log, target) {
    // Get the import set Sys ID from the current import set row
    var importSetSysId = source.sys_import_set.toString();

    // Log the import set Sys ID for debugging
    gs.addInfoMessage('Transform Script: Import Set Sys ID: ' + importSetSysId);

    // Set the import set reference field on the target record
    target.import_set = importSetSysId;
	gs.info('On before Transform map'+importSetSysId);
})(source, map, log, target);
